import 'dart:convert';
import 'package:fiveguysstore/application/config/router.dart';
import 'package:fiveguysstore/data/entity/product_entity.dart';
import 'package:flutter/material.dart';
import 'package:flutter/services.dart';
import 'package:go_router/go_router.dart';
import 'package:hooks_riverpod/hooks_riverpod.dart';
import 'package:shared_preferences/shared_preferences.dart';
import 'package:uuid/uuid.dart';

Future<void> setInitialData() async {
  final SharedPreferences prefs = await SharedPreferences.getInstance();
  final products = prefs.getStringList('products');
  if (products == null) {
    final initialProducts =
        [
          ProductEntity(
            id: Uuid().v4(),
            name: '자전거',
            price: 1000000.0,
            description: 'Description for Product 1',
            image:
                'data:image/jpeg;base64,',
          ),
          ProductEntity(
            id: Uuid().v4(),
            name: '맥북',
            price: 2000000.0,
            description: 'Description for Product 2',
            image:
                'data:image/jpeg;base64,',
          ),
          ProductEntity(
            id: Uuid().v4(),
            name: '아이폰 16 pro max',
            price: 1500000.0,
            description: 'Description for Product 3',
            image:
                'data:image/jpeg;base64,',
          ),
          ProductEntity(
            id: Uuid().v4(),
            name: '갤럭시 s25',
            price: 900000.0,
            description: 'Description for Product 4',
            image:
                'data:image/jpeg;base64,',
          ),
          ProductEntity(
            id: Uuid().v4(),
            name: '에어팟 프로',
            price: 300000.0,
            description: 'Description for Product 5',
            image:
                'data:image/jpeg;base64,',
          ),
        ].map((e) => jsonEncode(e.toJson())).toList();
    await prefs.setStringList('products', initialProducts);
  }
}

Future<void> main() async {
  WidgetsFlutterBinding.ensureInitialized();
  await setInitialData();
  //수직 모드 강제
  await SystemChrome.setPreferredOrientations([
    DeviceOrientation.portraitUp,
    DeviceOrientation.portraitDown,
  ]);
  final router = CustomRouter().createRouter();
  runApp(ProviderScope(child: StoreApp(router: router)));
}

class StoreApp extends StatelessWidget {
  const StoreApp({super.key, required this.router});
  final GoRouter router;

  @override
  Widget build(BuildContext context) {
    return MaterialApp.router(
      debugShowCheckedModeBanner: false,
      routerConfig: router,
      builder:
          //기기 별, 텍스트 사이즈 조절 불가 강제
          (context, child) => MediaQuery(
            data: MediaQuery.of(
              context,
            ).copyWith(textScaler: const TextScaler.linear(1.0)),
            child: child!,
          ),
    );
  }
}
